mapper.update:
UPDATE alarm
SET alarm_time = #{alarmTime}, weather_check = #{weatherCheck}, bbs_check = #{bbsCheck}, corona_check = #{coronaCheck}, schedule_check = #{scheduleCheck}, starluck_check = #{starluckCheck}
WHERE alarmNo = #{alarmNo} AND id = #{id}

mapper.select:
SELECT AUTO_INCREMENT
FROM information_schema.tables
WHERE table_name = "alarm" AND table_schema = DATABASE();
